import "./RegisterStyles.css";

import { Alert } from "@mui/material";
import { useState } from "react";
import InputField from "../../Components/Input/InputField";
import { useAppContext } from "../../Context/appContext";

//mui
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";

const Register = () => {
  const initialState = {
    name: "",
    email: "",
    password: "",
    isMember: true,
  };

  const [values, setValues] = useState(initialState);
  const { isLoading, showAlert, displayAlert, alertText, alertType } =
    useAppContext();

  const toggleMember = () => {
    setValues({ ...values, isMember: !values.isMember });
  };

  const handleChange = e => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const onSubmit = e => {
    e.preventDefault();
    const { name, email, password, isMember } = values;
    if (!email || !password || !isMember || !name) {
      displayAlert();
      return;
    }
  };

  return (
    <>
      <Container maxWidth="sm">
        <Box
          sx={{
            height: "100vh",
            bgcolor: "#cfe8fc",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <form onSubmit={onSubmit}>
            <div className="form-items">
              <h3 className="reg-header">
                {values.isMember ? "Bejelentkezés" : "Regisztráció"}
              </h3>
              {/* {showAlert && <Alert />} */}
              {!values.isMember && (
                <InputField
                  sx={{ m: "1em" }}
                  type="text"
                  name="name"
                  value={values.name}
                  handleChange={handleChange}
                  labelText="name"
                />
              )}
              <InputField
                sx={{ m: "1em" }}
                type="email"
                name="email"
                value={values.email}
                handleChange={handleChange}
                labelText="e-mail"
              />{" "}
              <InputField
                type="password"
                name="password"
                value={values.password}
                handleChange={handleChange}
                labelText="password"
              />
              <Button sx={{ m: "1em" }} variant="contained" type="submit">
                Belépés
              </Button>
            </div>
            <div className="reg-option">
              {values.isMember
                ? "Még nincs regisztrációd?"
                : "Már regisztráltam"}
              <Button
                sx={{ ml: "1em" }}
                type="button"
                variant="outlined"
                onClick={toggleMember}
              >
                {values.isMember ? "Regisztráció" : "Bejelentkezés"}
              </Button>
            </div>
          </form>
        </Box>
      </Container>
    </>
  );
};

export default Register;
